import React, { useState } from "react";
import { FaInstagram, FaFacebook, FaLine } from 'react-icons/fa';
import Logo_img from '../Components/Assets/logo.png';

const LoginSignup = () => {
  const [state, setState] = useState("Login"); // จัดการสถานะ Login หรือ Signup
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
  });

  const changeHandler = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const login = async () => {
    const { email, password } = formData;

    if (!email || !password) {
      alert("โปรดกรอก Email และ Password ก่อนเข้าใช้");
      return;
    }

    console.log("Login Function Executed", { formData });
    let responseData;
    await fetch("http://localhost:4000/login", {
      method: "POST",
      headers: {
        Accept: "application/form-data",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => (responseData = data));

    if (responseData.success) {
      localStorage.setItem("auth-token", responseData.token);
      window.location.replace("/shop");
    } else {
      alert(responseData.errors);
    }
  };

  const signup = async () => {
    console.log("Sign Up Function Executed", { formData });
    const { username, email, password } = formData;

    if (!username || !email || !password) {
      alert("กรุณากรอกข้อมูลให้ครบถ้วนทั้งชื่อผู้ใช้, อีเมล และ รหัสผ่าน");
      return;
    }

    try {
      const response = await fetch("http://localhost:4000/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify(formData),
      });

      const responseData = await response.json();

      if (responseData.success) {
        localStorage.setItem("auth-token", responseData.token);
        window.location.replace("/");
      } else {
        alert(responseData.errors || "สมัครสมาชิกไม่สำเร็จ กรุณาลองใหม่");
      }
    } catch (error) {
      console.error("เกิดข้อผิดพลาด:", error);
      alert("เกิดข้อผิดพลาด กรุณาลองใหม่ในภายหลัง");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-4xl flex flex-col md:flex-row bg-white shadow-lg rounded-lg overflow-hidden">
        <div className={`w-full md:w-1/2 p-8 space-y-6 ${state === "Signup" ? "order-last md:order-first" : ""}`}>
          <form onSubmit={(e) => e.preventDefault()} className="space-y-4">
            <div className="flex justify-center space-x-4 text-gray-600">
              <FaInstagram size={24} />
              <FaFacebook size={24} />
              <FaLine size={24} />
            </div>
            <h1 className="text-2xl font-bold text-center">{state === "Signup" ? "Create Account" : "Sign In"}</h1>
            {state === "Signup" && (
              <input
                type="text"
                name="username"
                placeholder="รหัสนักศึกษา"
                value={formData.username}
                onChange={changeHandler}
                className="w-full p-3 border rounded-lg focus:outline-none focus:ring focus:ring-purple-300"
              />
            )}
            <input
              type="email"
              name="email"
              placeholder="อีเมล"
              value={formData.email}
              onChange={changeHandler}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring focus:ring-purple-300"
            />
            <input
              type="password"
              name="password"
              placeholder="รหัสผ่าน"
              value={formData.password}
              onChange={changeHandler}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring focus:ring-purple-300"
            />
            <button
              type="button"
              onClick={state === "Signup" ? signup : login}
              className="w-full py-3 bg-purple-500 text-white rounded-lg hover:bg-purple-600 transition"
            >
              {state === "Signup" ? "Sign Up" : "Sign In"}
            </button>
          </form>
        </div>
        <div className="w-full md:w-1/2 bg-purple-500 text-white flex flex-col items-center justify-center space-y-4 p-8">
          <img src={Logo_img} alt="Logo" className="w-24 h-24" />
          <h1 className="text-2xl font-bold">{state === "Signup" ? "Welcome Back!" : "Hello Students!"}</h1>
          <p className="text-center">
            {state === "Signup"
              ? "Already have an account? Sign in to access your account."
              : "Don't have an account yet? Create one now!"}
          </p>
          <button
            onClick={() => setState(state === "Signup" ? "Login" : "Signup")}
            className="py-2 px-4 bg-white text-purple-500 rounded-lg hover:bg-gray-100 transition"
          >
            {state === "Signup" ? "Sign In" : "Sign Up"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
