import React, { createContext, useEffect, useState } from "react";

export const ShopContext = createContext(null);
const getDefaultCart = () => {
    let cart = {};
    for (let index = 0; index < 300 + 1; index++) {
        cart[index] = 0;
    }
    return cart;
}
const ShopContextProvider = (props) => {

    const [all_product, setAll_Product] = useState([]);
    const [cartItems, setCartItems] = useState(getDefaultCart());

    useEffect(() => {
        fetch('http://localhost:4000/allproducts')
            .then((response) => response.json())
            .then((data) => setAll_Product(data))

        if (localStorage.getItem('auth-token')) {
            fetch('http://localhost:4000/getcart', {
                method: 'POST',
                headers: {
                    Accept: 'application/form-data',
                    'auth-token': `${localStorage.getItem('auth-token')}`,
                    'Content-Type': 'application/json',
                },
                body: "",
            }).then((response) => response.json())
                .then((data) => setCartItems(data))
        }
    }, [])


    const addToCart = (itemId, size) => {
        setCartItems((prev) => {
            const existingItem = prev[itemId] || {};
            const updatedItem = {
                ...existingItem,
                [size]: (existingItem[size] || 0) + 1
            };
            return { ...prev, [itemId]: updatedItem };
        });

        if (localStorage.getItem('auth-token')) {
            fetch('http://localhost:4000/addtocart', {
                method: 'POST',
                headers: {
                    Accept: 'application/form-data',
                    'auth-token': `${localStorage.getItem('auth-token')}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ itemId, size }),
            })
                .then((response) => response.json())
                .then((data) => console.log(data));
        }
    };


    const removeToCart = (itemId, size) => {
        setCartItems((prev) => {
            const existingItem = prev[itemId] || {};
            const updatedSizeQuantity = (existingItem[size] || 0) - 1;

            // หากจำนวนเป็น 0 ให้ลบ size นั้นออก
            const updatedItem = updatedSizeQuantity > 0
                ? { ...existingItem, [size]: updatedSizeQuantity }
                : { ...existingItem };

            if (updatedSizeQuantity <= 0) delete updatedItem[size];

            return Object.keys(updatedItem).length > 0
                ? { ...prev, [itemId]: updatedItem }
                : { ...prev, [itemId]: undefined };
        });

        if (localStorage.getItem('auth-token')) {
            fetch('http://localhost:4000/removefromcart', {
                method: 'POST',
                headers: {
                    Accept: 'application/form-data',
                    'auth-token': `${localStorage.getItem('auth-token')}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ itemId, size }),
            })
                .then((response) => response.json())
                .then((data) => console.log(data));
        }
    };


    const getTotalCartAmount = () => {
        let totalAmount = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) {
                let itemInfo = all_product.find((product) => product.id === Number(item));
                if (itemInfo) { // ตรวจสอบว่า itemInfo ไม่ใช่ undefined
                    totalAmount += itemInfo.new_price * cartItems[item];
                }

            }
        }
        return totalAmount;
    }

    const getTotalCartItem = () => {
        let totalItem = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) {
                totalItem += cartItems[item];
            }
        }
        return totalItem;
    }



    const contextValue = { getTotalCartItem, getTotalCartAmount, all_product, cartItems, addToCart, removeToCart };

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    )
}

export default ShopContextProvider;
