import React from 'react';
import { Navigate } from 'react-router-dom';

const PrivateRoute = ({ children }) => {
    // ตรวจสอบ token จาก localStorage
    const token = localStorage.getItem('auth-token');
    const handleError = (message) => {
        triggerError(message); // เรียก Error Popup พร้อมข้อความที่ส่งมา
    };

    // ถ้าไม่มี token ให้แสดง alert และ redirect ไปที่หน้า login
    if (!token) {
        alert("คุณต้องเข้าสู่ระบบก่อนที่จะเข้าถึงหน้านี้");
        return <Navigate to="/" replace />;
    }

    // ถ้ามี token ให้แสดงหน้า
    return children;
};

export default PrivateRoute;
