import React, { useState, useEffect } from "react";
import { FaSpinner, FaTimes, FaCheckCircle } from "react-icons/fa"; // import FaTimes and FaCheckCircle for icons
import "./Error.css"; // Separate CSS file for styling

const ErrorPopup = ({ showError, errorMessage, showSuccess, successMessage, onClose }) => {
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        console.log("showError:", showError);
        if (showError) {
            setIsLoaded(false);
            const timer = setTimeout(() => {
                setIsLoaded(true);
                console.log("Spinner should be hidden, loading finished.");
            }, 200);
            return () => clearTimeout(timer);
        } else {
            setIsLoaded(false);
        }
    }, [showError]);

    return (
        (showError || showSuccess) && (
            <div className={`error-popup ${isLoaded ? "show" : ""}`}>
                {!isLoaded ? (
                    <FaSpinner className="spinner" /> // Show spinner before
                ) : showError ? (
                    <>
                        <div className="error-message">
                            <p>{errorMessage}</p>
                            <FaTimes className="close-icon" onClick={onClose} /> {/* Use FaTimes for close icon */}
                        </div>
                    </>
                ) : showSuccess ? (
                    <>
                        <div className="error-message">
                            <p>{successMessage}</p>
                            <FaCheckCircle className="success-icon" onClick={onClose} /> {/* Use FaCheckCircle for success icon */}
                        </div>
                    </>
                ) : null}
            </div>
        )
    );
};

export default ErrorPopup;
