import React, { useState } from "react";
import "./App.css";
import { Navbar } from "./Components/Navbar/Navbar";
import { Footer } from "./Components/Footer/Footer";
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import Shop from "./Pages/shop";
import ShopCategory from "./Pages/shopCategory";
import Product from "./Pages/Product";
import LoginSignup from "./Pages/LoginSignup";
import Cart from "./Pages/Cart";
import men_banner from "./Components/Assets/shirt.jpg";
import women_banner from "./Components/Assets/fashion3.webp";
import shop_banner from "./Components/Assets/shop.jpg";
import line_banner from "./Components/Assets/line.jpg";
import shoes_banner from "./Components/Assets/shoesbanner.jpg";
import ErrorPopup from "./ErrorPopup/Error";
import PrivateRoute from "./Private/Private"; // import PrivateRoute

function App() {
    const location = useLocation();
    const isLoginPage = location.pathname === "/";
    const [showError, setShowError] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");

    const triggerError = (message) => {
        setErrorMessage(message);
        setShowError(true);
    };

    const closeErrorPopup = () => setShowError(false);

    return (
        <div>
            <ErrorPopup
                showError={showError}
                errorMessage={errorMessage}
                onClose={closeErrorPopup}
            />
            {!isLoginPage && <Navbar />} {/* Hide Navbar on login page */}
            <Routes>
                <Route path="/" element={<LoginSignup triggerError={triggerError} />} />

                {/* PrivateRoute wraps around pages that need to be protected */}
                <Route
                    path="/shop"
                    element={<PrivateRoute><Shop /></PrivateRoute>}
                />
                <Route path="/mens" element={<ShopCategory banner={men_banner} category="ผู้ชาย" />} />
                <Route path="/womens" element={<ShopCategory banner={women_banner} category="ผู้หญิง" />} />
                <Route path="/shirt" element={<ShopCategory banner={shop_banner} category="เสื้อช็อป" />} />
                <Route path="/ruls" element={<ShopCategory banner={line_banner} category="เข็มขัด" />} />
                <Route path="/shose" element={<ShopCategory banner={shoes_banner} category="รองเท้า" />} />
                <Route path="product" element={<Product />}>
                    <Route path=":productId" element={<Product />} />
                </Route>
                <Route path="/cart" element={<Cart />} />
            </Routes>
            {!isLoginPage && <Footer />} {/* Hide Footer on login page */}
        </div>
    );
}

export default function WrappedApp() {
    return (
        <BrowserRouter>
            <App />
        </BrowserRouter>
    );
}
