import React, { useState, useEffect } from "react";
import { FaSpinner, FaTimes, FaCheckCircle } from "react-icons/fa"; // เพิ่ม FaCheckCircle สำหรับ success icon
import "./Notification.css"; // เปลี่ยนชื่อไฟล์ CSS ให้รองรับทั้ง error และ success

const NotificationPopup = ({
    showError,
    errorMessage,
    showSuccess,
    successMessage,
    onClose
}) => {
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        if (showError || showSuccess) {
            setIsLoaded(false);
            const timer = setTimeout(() => {
                setIsLoaded(true);
            }, 200);
            return () => clearTimeout(timer);
        } else {
            setIsLoaded(false);
        }
    }, [showError, showSuccess]);

    return (
        (showError || showSuccess) && (
            <div
                className={`error-popup ${isLoaded ? "show" : ""
                    } ${showError ? "error" : "success"}`}
            >
                <div className="error-message">
                    {!isLoaded ? (
                        <FaSpinner className="spinner" />
                    ) : showError ? (
                        // แสดง error popup
                        <>
                            <p>{errorMessage}</p>
                            <FaTimes
                                className="close-icon"
                                onClick={onClose}
                            />
                        </>
                    ) : (
                        // แสดง success popup
                        <>
                            <FaCheckCircle className="success-icon" />
                            <p>{successMessage}</p>
                            <FaTimes
                                className="close-icon"
                                onClick={onClose}
                            />
                        </>
                    )}
                </div>
            </div>
        )
    );
};

export default NotificationPopup;
