import React, { useContext, useState } from 'react';
import './CartItems.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';
import add_icon from '../Assets/addcart.png';

export const CartItems = ({ triggerError }) => {
    const { getTotalCartAmount, all_product, cartItems, removeToCart, addToCart, user } = useContext(ShopContext);
    const [showPopup, setShowPopup] = useState(false); // State สำหรับจัดการ popup


    const handleError = (message) => {
        triggerError(message); // เรียก Error Popup พร้อมข้อความที่ส่งมา
    };

    if (!all_product || !cartItems) {
        return <div>No products available</div>;
    }

    // ฟังก์ชันสำหรับยืนยันการสั่งซื้อ
    const handleConfirmOrder = async () => {
        try {
            const orderItems = all_product
                .filter((e) => cartItems[e.id] > 0)
                .map((e) => ({
                    productId: e.id,
                    name: e.name,
                    quantity: cartItems[e.id],
                    total: e.new_price * cartItems[e.id],
                }));

            const orderData = {
                name: user.name, // สมมติ user มีข้อมูล name ใน context
                idstudent: user.idstudent,
                totalAmount: getTotalCartAmount(),
                items: orderItems,
            };

            // ส่งข้อมูลคำสั่งซื้อไปยัง backend
            const response = await fetch('/api/orders', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(orderData),
            });

            if (response.ok) {
                alert('คำสั่งซื้อสำเร็จ!');
                setShowPopup(false); // ปิด popup หลังจากยืนยัน
            } else {
                alert('เกิดข้อผิดพลาดในการสั่งซื้อ');
            }
        } catch (error) {
            console.error('Error confirming order:', error);
            handleError('เกิดข้อผิดพลาดในการสั่งซื้อ');
        }
    };

    return (
        <div className="cartitem">
            <div className="cartitem-format-main grid grid-cols-7 gap-4 px-4 py-2">
                <span className="span col-span-1">สินค้า</span>
                <span className="span col-span-2">ชื่อสินค้า</span>
                <span className="span col-span-1">ราคา</span>
                <span className="span col-span-1">จำนวน</span>
                <span className="span col-span-1">รวม</span>
                <span className="span col-span-1">เพิ่ม & ลบ สินค้า</span>
                <span className="total col-span-1">ราคารวมสินค้า</span>
            </div>
            <hr />
            {all_product.map((e) => {
                if (cartItems[e.id] > 0) {
                    return (
                        <div key={e.id}>
                            <div className="cartitem-format cartitem-format-main grid grid-cols-7 gap-4 items-center px-4 py-2">
                                <img src={e.image} alt="" className="carticon-product-icon col-span-1" />
                                <p className="cartitem-name col-span-2">{e.name}</p>
                                <p className="col-span-1">{e.new_price} <span>บาท</span></p>
                                <button className="cartitems-quantity col-span-1">{cartItems[e.id]}</button>
                                <p className="col-span-1">{e.new_price * cartItems[e.id]}</p>
                                <img
                                    className="add-icon col-span-1 cursor-pointer"
                                    src={add_icon}
                                    onClick={() => { addToCart(e.id); }}
                                    alt=""
                                />
                                <img
                                    className="cartitems-remove-icon col-span-1 cursor-pointer"
                                    src={remove_icon}
                                    onClick={() => { removeToCart(e.id); }}
                                    alt=""
                                />
                            </div>
                            <hr />
                        </div>
                    );
                }
                return null;
            })}
            <div className="cartitems-down mt-4">
                <div className="cartitems-total">
                    <h1 className="text-xl">ยอดรวมสินค้า</h1>
                    <div>
                        <div className="cartitem-total-item">
                            <p>ผลรวม</p>
                            <p>${getTotalCartAmount()}</p>
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <p>นัดรับเอง</p>
                            <p>Free</p>
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <h3>ราคา</h3>
                            <h3>${getTotalCartAmount()}</h3>
                        </div>
                    </div>
                    <button onClick={() => setShowPopup(true)} className="mt-4 px-6 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600">
                        <span>สั่งซื้อสินค้า</span>
                    </button>
                </div>
            </div>

            {showPopup && (
                <div className="popup fixed inset-0 flex justify-center items-center bg-gray-700 bg-opacity-50">
                    <div className="popup-content bg-white p-6 rounded-lg shadow-lg w-full max-w-lg">
                        <h2 className="text-xl font-semibold">ยืนยันคำสั่งซื้อ</h2>
                        <div className="popup-items mt-4">
                            {all_product.map((e) => {
                                if (cartItems[e.id] > 0) {
                                    return (
                                        <div key={e.id} className="popup-item flex items-center justify-between mb-4">
                                            <img src={e.image} alt="" className="w-16 h-16 object-cover mr-4" />
                                            <div>
                                                <p>{e.name}</p>
                                                <p>จำนวน: {cartItems[e.id]}</p>
                                                <p>รวม: {e.new_price * cartItems[e.id]} บาท</p>
                                            </div>
                                        </div>
                                    );
                                }
                                return null;
                            })}
                        </div>
                        <div className="popup-total mt-4">
                            <h3>ยอดรวมทั้งหมด: {getTotalCartAmount()} บาท</h3>
                        </div>
                        <div className="flex justify-between mt-4">
                            <button
                                onClick={handleConfirmOrder}
                                className="px-6 py-2 bg-green-500 text-white rounded-md hover:bg-green-600"
                            >
                                ยืนยันคำสั่งซื้อ
                            </button>
                            <button
                                onClick={() => setShowPopup(false)}
                                className="px-6 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
                            >
                                ยกเลิก
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>

    );
};

export default CartItems;
