import React, { useContext, useRef, useState } from 'react';
import './Navbar.css';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { Link } from 'react-router-dom';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/icon-dropdown.png';

export const Navbar = () => {
    const [menuVisible, setMenuVisible] = useState(false);
    const { getTotalCartItem } = useContext(ShopContext);

    const toggleMenu = () => {
        setMenuVisible(!menuVisible);
    };

    return (
        <div className='navbar'>
            <div className="navbar-logo">
                <Link to='/shop'><img src={logo} alt="Logo" /></Link>
                <p>เครื่องแบบนักศึกษา</p>
            </div>
            <div className="nav-dropdown" onClick={toggleMenu}>
                <img src={nav_dropdown} alt="Menu" />
            </div>
            <ul className={`nav-menu ${menuVisible ? 'nav-menu-visible' : ''}`}>
                {['shop', 'mens', 'womens', 'shirt', 'ruls', 'shose'].map((item) => (
                    <li key={item}>
                        <Link to={`/${item}`}>
                            <p>{item}</p>
                        </Link>
                        <hr />
                    </li>
                ))}
            </ul>
            <div className="nav-login-cart">
                {localStorage.getItem('auth-token') ? (
                    <button onClick={() => { localStorage.removeItem('auth-token'); window.location.replace('/'); }}>
                        Logout
                    </button>
                ) : (
                    <Link to='/'>
                        <button>ลงชื่อเข้าใช้</button>
                    </Link>
                )}
                <Link className='carticon' to='/cart'><img src={cart_icon} alt="Cart" /></Link>
                <div className="nav-cart-count">{getTotalCartItem()}</div>
            </div>
        </div>
    );
};
