import React, { useState, useEffect } from "react";
import { FaSpinner, FaTimes, FaCheckCircle } from "react-icons/fa";
import "./Error.css"; // CSS สำหรับทั้งสอง Popup

// Error Popup
const ErrorPopup = ({ showError, errorMessage, onClose }) => {
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        if (showError) {
            setIsLoaded(false);
            const timer = setTimeout(() => {
                setIsLoaded(true);
            }, 200);
            return () => clearTimeout(timer);
        } else {
            setIsLoaded(false);
        }
    }, [showError]);

    return (
        showError && (
            <div className={`error-popup ${isLoaded ? "show" : ""}`}>
                <div className="error-message">
                    {!isLoaded ? (
                        <FaSpinner className="spinner" />
                    ) : (
                        <>
                            <p>{errorMessage}</p>
                            <FaTimes className="close-icon" onClick={onClose} />
                        </>
                    )}
                </div>
            </div>
        )
    );
};

// Success Popup
const SuccessPopup = ({ showSuccess, successMessage, onClose }) => {
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        if (showSuccess) {
            setIsLoaded(false);
            const timer = setTimeout(() => {
                setIsLoaded(true);
            }, 200);
            return () => clearTimeout(timer);
        } else {
            setIsLoaded(false);
        }
    }, [showSuccess]);

    return (
        showSuccess && (
            <div className={`success-popup ${isLoaded ? "show" : ""}`}>
                <div className="success-message">
                    {!isLoaded ? (
                        <FaSpinner className="spinner" />
                    ) : (
                        <>
                            <FaCheckCircle className="success-icon" />
                            <p>{successMessage}</p>
                            <FaTimes className="close-icon" onClick={onClose} />
                        </>
                    )}
                </div>
            </div>
        )
    );
};

export { ErrorPopup, SuccessPopup };
