import React, { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa"; // import spinner icon
import "./Error.css"; // ไฟล์ CSS แยกสำหรับ styling

const ErrorPopup = ({ showError, errorMessage, onClose }) => {
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        if (showError) {
            setIsLoaded(false); // เริ่มต้นด้วย spinner
            const timer = setTimeout(() => {
                setIsLoaded(true); // หลังจาก 500ms ให้แสดงข้อความ
            }); // เวลาหน่วง 500ms เพื่อแสดง spinner

            return () => clearTimeout(timer); // เคลียร์ timeout เมื่อ unmount หรือ showError เปลี่ยน
        } else {
            setIsLoaded(false); // รีเซ็ตเมื่อ popup ปิด
        }
    }, [showError]);

    return (
        showError && (
            <div className={`error-popup ${isLoaded ? "show" : ""}`}>
                <div className="error-message">
                    {!isLoaded ? (
                        <FaSpinner className="spinner" /> // แสดง spinner ก่อน
                    ) : (
                        <>
                            <p>{errorMessage}</p>
                            <button onClick={onClose}>Close</button>
                        </>
                    )}
                </div>
            </div>
        )
    );
};

export default ErrorPopup;
