import React, { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa"; // เพิ่มไอคอน spinner
import "./Error.css"; // ไฟล์ CSS แยกสำหรับ styling

const ErrorPopup = ({ showError, errorMessage, onClose }) => {
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        if (showError) {
            // หน่วงเวลา 500ms ก่อนแสดงข้อความ
            const timer = setTimeout(() => {
                setIsLoaded(true);
            }, 200); // เวลาที่ต้องการหน่วง (500ms)

            return () => clearTimeout(timer); // เคลียร์ timeout เมื่อ component ถูก unmount หรือ showError เปลี่ยน
        } else {
            setIsLoaded(false); // รีเซ็ตเมื่อปิด popup
        }
    }, [showError]);

    return (
        showError && (
            <div className={`error-popup ${isLoaded ? "show" : ""}`}>
                <div className="error-message">
                    {!isLoaded ? (
                        <FaSpinner className="spinner" /> // แสดงไอคอนหมุนก่อน
                    ) : (
                        <p>{errorMessage}</p> // เมื่อโหลดเสร็จแล้วให้แสดงข้อความ
                    )}
                    {isLoaded && <button onClick={onClose}>Close</button>}
                </div>
            </div>
        )
    );
};

export default ErrorPopup;
