import React, { useState } from "react";
import "./CSS/LoginSignUp.css";
import Logo_img from '../Components/Assets/logo.png';
import { FaInstagram, FaFacebook, FaLine } from 'react-icons/fa';
import { FaSpinner } from 'react-icons/fa'; // Icon สำหรับแสดง loading

const LoginSignup = () => {
  const [state, setState] = useState("Login");
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
  });
  const [error, setError] = useState("");
  const [showError, setShowError] = useState(false);
  const [isLoading, setIsLoading] = useState(false); // เพิ่ม state สำหรับ loading
  const [loading, setLoading] = useState(false);


  const changeHandler = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const login = async () => {
    const { email, password } = formData;

    if (!email || !password) {
      setError("โปรดกรอก Email และ Password ก่อนเข้าใช้");
      setShowError(true);
      return;
    }

    setLoading(true); // เปิด loading

    let responseData;
    try {
      const response = await fetch("http://localhost:4000/login", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      responseData = await response.json();

      if (responseData.success) {
        const role = responseData.role;
        if (role === "user") {
          localStorage.setItem("auth-token", responseData.token);
          window.location.replace("/shop");
        } else {
          setError("บัญชีนี้ไม่ได้รับอนุญาตให้เข้าสู่ระบบ");
          setShowError(true);
        }
      } else {
        setError(responseData.errors || "เกิดข้อผิดพลาดในการเข้าสู่ระบบ");
        setShowError(true);
      }
    } catch (error) {
      console.error("Error:", error);
      setError("เกิดข้อผิดพลาดในการเข้าสู่ระบบ");
      setShowError(true);
    } finally {
      setLoading(false); // ปิด loading
    }
  };

  const signup = async () => {
    const { username, email, password, role } = formData;

    if (!username || !email || !password) {
      setError("กรุณากรอกข้อมูลให้ครบถ้วนทั้งชื่อผู้ใช้, อีเมล และ รหัสผ่าน");
      setShowError(true);
      return;
    }

    setLoading(true); // เปิด loading

    try {
      const response = await fetch("http://localhost:4000/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({ username, email, password, role: role || "user" }),
      });

      const responseData = await response.json();

      if (responseData.success) {
        localStorage.setItem("auth-token", responseData.token);
        window.location.replace("/");
      } else {
        setError(responseData.errors || "สมัครสมาชิกไม่สำเร็จ กรุณาลองใหม่");
        setShowError(true);
      }
    } catch (error) {
      console.error("เกิดข้อผิดพลาด:", error);
      setError("เกิดข้อผิดพลาด กรุณาลองใหม่ในภายหลัง");
      setShowError(true);
    } finally {
      setLoading(false); // ปิด loading
    }
  };


  const closeErrorPopup = () => {
    setShowError(false);
  };

  return (
    <div className="center">
      {loading && (
        <div className="loading-overlay">
          <div className="spinner">🔄</div>
        </div>
      )}
      {showError && (
        <div className={`error-popup ${showError ? "show" : ""}`}>
          <div className="error-message">
            <p>{error}</p>
            <button onClick={closeErrorPopup}>Close</button>
          </div>
        </div>
      )}
      <div className={`container ${state === "Signup" ? "active" : ""}`} id="container">
        {/* Sign-up form */}
        <div className="form-container sign-up">
          <form onSubmit={(e) => e.preventDefault()}>
            <div className="footer-social-icon">
              <div className="footer-icons-container">
                <span><FaInstagram size={24} /></span>
              </div>
              <div className="footer-icons-container">
                <span><FaFacebook size={24} /></span>
              </div>
              <div className="footer-icons-container">
                <span><FaLine size={24} /></span>
              </div>
            </div>
            <h1 className="for-h1">Create Account</h1>
            <input
              type="text"
              name="username"
              placeholder="รหัสนักศึกษา"
              value={formData.username}
              onChange={changeHandler}
            />
            <input
              type="email"
              name="email"
              placeholder="อีเมล"
              value={formData.email}
              onChange={changeHandler}
            />
            <input
              type="password"
              name="password"
              placeholder="รหัสผ่าน"
              value={formData.password}
              onChange={changeHandler}
            />
            <button type="button" onClick={signup}>
              Sign Up
            </button>
          </form>
        </div>

        {/* Sign-in form */}
        <div className="form-container sign-in">
          <form onSubmit={(e) => e.preventDefault()}>
            <div className="footer-social-icon">
              <div className="footer-icons-container">
                <span><FaInstagram size={24} /></span>
              </div>
              <div className="footer-icons-container">
                <span><FaFacebook size={24} /></span>
              </div>
              <div className="footer-icons-container">
                <span><FaLine size={24} /></span>
              </div>
            </div>
            <h1 className="for-h1">Sign In</h1>
            <input
              type="email"
              name="email"
              placeholder="อีเมล"
              value={formData.email}
              onChange={changeHandler}
            />
            <input
              type="password"
              name="password"
              placeholder="รหัสผ่าน"
              value={formData.password}
              onChange={changeHandler}
            />
            <button type="button" onClick={login}>
              Sign In
            </button>
          </form>
        </div>

        <div className="toggle-container">
          <div className="toggle">
            <div className="toggle-panel toggle-left">
              <img className="toggle-logo" src={Logo_img} alt="" />
              <h1>ยินดีต้อนรับ!</h1>
              <p>หลังจากกรอกรายละเอียดเรียบร้อยแล้ว <br /> สามารถคลิ๊กที่ปุ่ม Sing Up เพื่อเข้าใช้งานได้เลย</p>
              <button className="hidden" onClick={() => setState("Login")}>
                Sign In
              </button>
            </div>
            <div className="toggle-panel toggle-right">
              <img className="toggle-logo" src={Logo_img} alt="" />
              <h1>สวัสดีนักศึกษาทุกท่าน</h1>
              <p>หากยังไม่ได้สมัครสมาชิกสามารถคลิ๊กที่ปุ่มสมัครด้านล่างได้เลย!</p>
              <button className="hidden" onClick={() => setState("Signup")}>
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
