import React, { useContext, useState } from 'react';
import './CartItems.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';
import add_icon from '../Assets/addcart.png';

export const CartItems = ({ triggerError }) => {
    const { getTotalCartAmount, all_product, cartItems, removeToCart, addToCart, user } = useContext(ShopContext);
    const [showPopup, setShowPopup] = useState(false); // State for managing popup

    if (!all_product || !cartItems) {
        triggerError("No products available");
        return <div>No products available</div>;
    }

    const handleError = (message) => {
        triggerError(message); // Trigger error popup with the given message
    };

    // Function to confirm order
    const handleConfirmOrder = async () => {
        try {
            const orderItems = all_product
                .filter((e) => cartItems[e.id] > 0)
                .map((e) => ({
                    productId: e.id,
                    name: e.name,
                    quantity: cartItems[e.id],
                    total: e.new_price * cartItems[e.id],
                }));

            const orderData = {
                name: user.name, // Assume user has name in context
                idstudent: user.idstudent,
                totalAmount: getTotalCartAmount(),
                items: orderItems,
            };

            // Send order data to backend
            const response = await fetch('/api/orders', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(orderData),
            });

            if (response.ok) {
                alert('Order successful!');
                setShowPopup(false); // Close popup after confirmation
            } else {
                handleError('There was an error processing your order');
            }
        } catch (error) {
            console.error('Error confirming order:', error);
            handleError('There was an error processing your order');
        }
    };

    return (
        <div className='cartitem'>
            <div className="cartitem-format-main">
                <span className='span'>Product</span>
                <span className='span'>Product Name</span>
                <span className='span'>Price</span>
                <span className='span'>Quantity</span>
                <span className='span'>Total</span>
                <span className='span'>Add & Remove</span>
                <span className='total'>Total Price</span>
            </div>
            <hr />
            {all_product.map((e) => {
                if (cartItems[e.id] > 0) {
                    return (
                        <div key={e.id}>
                            <div className="cartitem-format cartitem-format-main">
                                <img src={e.image} alt="" className='carticon-product-icon' />
                                <p className='cartitem-name'>{e.name}</p>
                                <p>{e.new_price} <span>Baht</span></p>
                                <button className='cartitems-quantity'>{cartItems[e.id]}</button>
                                <p>{e.new_price * cartItems[e.id]}</p>
                                <img className='add-icon' src={add_icon} onClick={() => { addToCart(e.id) }} alt="Add to cart" />
                                <img className='cartitems-remove-icon' src={remove_icon} onClick={() => { removeToCart(e.id) }} alt="Remove from cart" />
                            </div>
                            <hr />
                        </div>
                    );
                }
                return null;
            })}
            <div className="cartitems-down">
                <div className="cartitems-total">
                    <h1>Cart Total</h1>
                    <div>
                        <div className="cartitem-total-item">
                            <p>Subtotal</p>
                            <p>{getTotalCartAmount()} Baht</p>
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <p>Self Pickup</p>
                            <p>Free</p>
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <h3>Total Price</h3>
                            <h3>{getTotalCartAmount()} Baht</h3>
                        </div>
                    </div>
                    <button onClick={() => setShowPopup(true)}><span>Confirm Order</span></button>
                </div>
            </div>

            {showPopup && (
                <div className="popup">
                    <div className="popup-content">
                        <h2>Confirm Order</h2>
                        <div className="popup-items">
                            {all_product.map((e) => {
                                if (cartItems[e.id] > 0) {
                                    return (
                                        <div key={e.id} className="popup-item">
                                            <img src={e.image} alt="" />
                                            <p>{e.name}</p>
                                            <p>Quantity: {cartItems[e.id]}</p>
                                            <p>Total: {e.new_price * cartItems[e.id]} Baht</p>
                                        </div>
                                    );
                                }
                                return null;
                            })}
                        </div>
                        <div className="popup-total">
                            <h3>Total Amount: {getTotalCartAmount()} Baht</h3>
                        </div>
                        <button onClick={handleConfirmOrder}>Confirm Order</button>
                        <button onClick={() => setShowPopup(false)}>Cancel</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CartItems;
